const video = document.querySelector('.premier__video-video');
const playBth = document.querySelector('.premier__video-button');


function toogleVideoStatus() {
  if (video.paused) {
      video.play()
  } else {
      video.pause()
  }
}

playBth.addEventListener('click', toogleVideoStatus)

let play = document.querySelector('#on');
let pause = document.querySelector('#off');
let themeButton = document.querySelector('.premier__video-button');

themeButton.onclick = function() {
  play.classList.toggle('none');
  pause.classList.toggle('none');
 
};

